mutation LinkUserMutation(
  $channelID: ID!
  $extensionID: ID!
  $token: String!
  $showUser: Boolean!
) {
  extensionLinkUser(input: {
    channelID: $channelID,
    extensionID: $extensionID,
    jwt: $token,
    showUser: $showUser,
  }) {
    token {
      extensionID
      jwt
    }
    helixToken {
      jwt
    }
  }
}
